 $sql1 = "INSERT INTO orcamentos (nome, email, telefone, cidade, estado, descricao, data_envio) VALUES (?, ?, ?, ?, ?, ?, NOW())";    $sql2 = "INSERT INTO orcamentos (nome, email, telefone, cidade, estado, descricao, data_envio) VALUES (?, ?, ?, ?, ?, ?, NOW())";    $stmt1 = $conexao1->prepare($sql1);    $stmt2 = $conexao2->prepare($sql2);    if ($stmt1 && $stmt2) {        $stmt1->bind_param("ssssss", $nome, $email, $telefone_completo, $cidade, $estado, $descricao);        $stmt2->bind_param("ssssss", $nome, $email, $telefone_completo, $cidade, $estado, $descricao);        if ($stmt1->execute() && $stmt2->execute()) {            $mail = new PHPMailer(true);            try {                $mail->isSMTP();                $mail->Host = 'mail.embrafer.com';                $mail->SMTPAuth = true;                $mail->Username = 'contato@embrafer.com';                $mail->Password = 'eneastroca8081!';                $mail->SMTPSecure = 'ssl';                $mail->Port = 465;                $mail->SMTPOptions = [                    'ssl' => [                        'verify_peer' => false,                        'verify_peer_name' => false,                        'allow_self_signed' => true,                    ],                ];                $mail->setFrom('contato@embrafer.com', 'Embrafer Contato');                $mail->addAddress('contato@embrafer.com', 'Embrafer');                $mail->isHTML(true);                $mail->CharSet = 'UTF-8';                $mail->Subject = 'Novo Contato - Site Empresarial';                $mail->Body = "                    <html>                    <body>                        <h3>Contato recebido pelo site</h3>                        <p><strong>Nome:</strong> $nome</p>                        <p><strong>E-mail:</strong> $email</p>                        <p><strong>Telefone:</strong> $telefone_completo</p>                        <p><strong>Cidade:</strong> $cidade</p>                    